package java_progs;

public class increment_decrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// increment operator
		int a=10;
		System.out.println(a++);
		System.out.println(a);
		System.out.println(++a);
		System.out.println(a);
// decrement operator
		int b=10;
		System.out.println(b--);
		System.out.println(b);
		System.out.println(--b);
		System.out.println(b);
// quick quiz
		int y=7;
		int x=++y*8;
		System.out.println(x);
	char a='b';
	System.out.println(++a);
	}

}
